import React, { useEffect, useState } from "react";
import {
    Card,
    CardBody,
    CardHeader,
    Col,
    Container,
    FormGroup,
    Input,
    Label,
    Row,
} from "reactstrap";
import Select from "react-select";
import { MetaTags } from "react-meta-tags";
import { useDispatch, useSelector } from "react-redux";
import {
    AlertState,
    Breadcrumb_inputName,
    commonPageField,
    commonPageFieldSuccess,
} from "../../../store/actions";
import { useHistory } from "react-router-dom";
import {
    editPartyItemIDSuccess,
    getpartyItemList,
    getPartyItemListSuccess,
    SavePartyItems,
    SavePartyItemsSuccess
} from "../../../store/Administrator/PartyItemsRedux/action";
import paginationFactory, { PaginationListStandalone, PaginationProvider } from "react-bootstrap-table2-paginator";
import ToolkitProvider from "react-bootstrap-table2-toolkit";
import { countlabelFunc } from "../../../components/Common/CommonMasterListPage";
import { mySearchProps } from "../../../components/Common/SearchBox/MySearch";
import { SaveButton } from "../../../components/Common/CommonButton";
import { comAddPageFieldFunc, formValid, initialFiledFunc, onChangeSelect, } from "../../../components/Common/validationFunction";
import * as url from "../../../routes/route_url";
import * as mode from "../../../routes/PageMode";
import BootstrapTable from "react-bootstrap-table-next";
import { getPartyListAPI } from "../../../store/Administrator/PartyRedux/action";
import { CustomAlert } from "../../../CustomAlert/ConfirmDialog";
import { breadcrumbReturnFunc, btnIsDissablefunc, loginIsSCMCompany, loginPartyID } from "../../../components/Common/CommonFunction";
import * as pageId from "../../../routes/allPageID";
import { selectAllCheck } from "../../../components/Common/TableCommonFunc";

const PartyItems = (props) => {

    const history = useHistory()
    const dispatch = useDispatch();
    const [pageMode, setPageMode] = useState(mode.defaultsave);
    const [modalCss, setModalCss] = useState(false);
    const [userAccState, setUserAccState] = useState("");

    const fileds = {
        id: "",
        Name: loginIsSCMCompany() === 1 ? { value: loginPartyID() } : ""
    }

    const [state, setState] = useState(() => initialFiledFunc(fileds))

    const values = { ...state.values }
    const { isError } = state;
    const { fieldLabel } = state;


    const location = { ...history.location }
    const hasShowloction = location.hasOwnProperty(mode.editValue)
    const hasShowModal = props.hasOwnProperty(mode.editValue)


    //Access redux store Data /  'save_ModuleSuccess' action data
    const {
        postMsg,
        updateMsg,
        supplier,
        pageField,
        tableList,
        userAccess } = useSelector((state) => ({
            postMsg: state.PartyItemsReducer.postMsg,
            updateMsg: state.PartyItemsReducer.updateMsg,
            tableList: state.PartyItemsReducer.partyItem,
            supplier: state.PartyMasterReducer.partyList,
            userAccess: state.Login.RoleAccessUpdateData,
            pageField: state.CommonPageFieldReducer.pageField
        }));

    useEffect(() => {
        const page_Id = pageId.PARTYITEM
        dispatch(getPartyItemListSuccess([]))
        dispatch(commonPageFieldSuccess(null));
        dispatch(commonPageField(page_Id))
        if (!(hasShowloction && hasShowModal)) {
            dispatch(getPartyListAPI())
        }
    }, []);

    // userAccess useEffect
    useEffect(() => {
        let userAcc = null;
        let locationPath = location.pathname;

        if (hasShowModal) {
            locationPath = props.masterPath;
        }

        userAcc = userAccess.find((inx) => {
            return (`/${inx.ActualPagePath}` === locationPath)
        })

        if (userAcc) {
            setUserAccState(userAcc);
            if (!props.isAssing) {
                breadcrumbReturnFunc({ dispatch, userAcc });
            }
        };
    }, [userAccess])

    // This UseEffect 'SetEdit' data and 'autoFocus' while this Component load First Time.
    useEffect(() => {

        if ((hasShowloction || hasShowModal)) {

            let hasEditVal = null
            if (hasShowModal) {
                hasEditVal = props.editValue
                setPageMode(props.pageMode)
                setModalCss(true)
            }
            else if (hasShowloction) {
                setPageMode(location.pageMode)
                hasEditVal = location.editValue
            }
            if (hasEditVal) {
                const { Party, PartyName, PartyItem = [] } = hasEditVal
                const { values, fieldLabel, hasValid, required, isError } = { ...state }

                hasValid.Name.valid = true;
                values.Name = { value: Party, label: PartyName };

                const convArr = PartyItem.map((item) => {
                    item["selectCheck"] = false
                    if (item.Party > 0) {
                        { item["selectCheck"] = true }
                    }
                    return item
                });
                dispatch(getPartyItemListSuccess(convArr))
                
                setState({ values, fieldLabel, hasValid, required, isError })
                dispatch(Breadcrumb_inputName(PartyName))
            }
            dispatch(editPartyItemIDSuccess({ Status: false }))
        }
        else if (loginIsSCMCompany() === 1) {

            setState((i) => {
                const a = { ...i }
                a.values.Name = { value: loginPartyID(), label: '' }
                a.hasValid.Name.valid = true
                return a
            })
            dispatch(getpartyItemList(loginPartyID()))
        }
    }, [])

    useEffect(async () => {
        if ((postMsg.Status === true) && (postMsg.StatusCode === 200)) {
            dispatch(SavePartyItemsSuccess({ Status: false }))
            if (pageMode === mode.assingLink) {
                CustomAlert({
                    Type: 1,
                    Message: postMsg.Message,
                })
                props.isOpenModal(false)
            }
            else if (pageMode === mode.edit) {
                CustomAlert({
                    Type: 1,
                    Message: postMsg.Message,
                })
                history.push({ pathname: url.PARTYITEM_LIST })
            }
            else {
                dispatch(Breadcrumb_inputName(''))
                const promise = await CustomAlert({
                    Type: 1,
                    Message: postMsg.Message,
                })
                if (promise) { history.push({ pathname: url.PARTYITEM_LIST }) }
            }

        } else if
            (postMsg.Status === true) {
            CustomAlert({
                Type: 3,
                Message: JSON.stringify(postMsg.Message),
            })
        }
    }, [postMsg])

    useEffect(() => {

        if (updateMsg.Status === true && updateMsg.StatusCode === 206550 && !modalCss) {
            history.push({
                pathname: url.PARTYITEM_LIST,
            })
        } else if (updateMsg.Status === true && !modalCss) {
            dispatch(SavePartyItemsSuccess({ Status: false }));
            dispatch(
                AlertState({
                    Type: 3,
                    Status: true,
                    Message: JSON.stringify(updateMsg.Message),
                })
            );
        }
    }, [updateMsg, modalCss]);

    useEffect(() => {

        if (pageField) {
            const fieldArr = pageField.PageFieldMaster
            comAddPageFieldFunc({ state, setState, fieldArr })
        }
    }, [pageField])

    const supplierOptions = supplier.map((i) => ({
        value: i.id,
        label: i.Name,
    }));

    const tableColumns = [
        {
            text: "Item ID",
            dataField: "Item",
            sort: true,
        },
        {
            text: "Item Name",
            dataField: "ItemName",
            sort: true,
        },
        // {
        //     text: "SelectAll",
        //     dataField: "itemCheck",
        //     sort: true,
        //     formatter: (cellContent, row, col, k) => {


        //         if ((row["hasInitialVal"] === undefined)) { row["hasInitialVal"] = cellContent }

        //         return (<span >
        //             <Input type="checkbox"
        //                 defaultChecked={cellContent}
        //                 key={row.Item}
        //                 disabled={(pageMode === mode.assingLink) ? (row.hasInitialVal) ? true : false : false}
        //                 onChange={e => {
        //                     setitemArr(ele => {
        //                         let a = { ...ele };
        //                         const newrr = [...ele].map(i => {
        //                             if (row.Item === i.Item) {
        //                                 i.itemCheck = !i.itemCheck;
        //                             }
        //                             return i
        //                         });
        //                         return newrr
        //                     })

        //                 }}
        //             />

        //         </span>
        //         )
        //     },

        // }
    ];

    const pageOptions = {
        sizePerPage: 15,
        custom: true,
    };

    const GoButton_Handler = async (e) => {
        let supplier = e.value
        if (!supplier > 0) {
            alert("Please Select Supplier")
            return
        }

        if (tableList.length > 0) {
            const ispermission = await CustomAlert({ Type: 7, Message: "Refresh  Item...!" })
            if (ispermission) {
                dispatch(getPartyItemListSuccess([]))
            } else {
                return
            }
        }

        dispatch(getpartyItemList(supplier))
    };

    const SaveHandler = async (event) => {
        event.preventDefault();
        const Find = tableList.filter((index) => {
            return (index.selectCheck === true)
        })
        const btnId = event.target.id
        try {
            if (formValid(state, setState)) {
                btnIsDissablefunc({ btnId, state: true })
                var PartyData = Find.map((index) => ({
                    Item: index.Item,
                    Party: values.Name.value
                }))
                const jsonBody = JSON.stringify(PartyData)
                dispatch(SavePartyItems({ jsonBody, btnId }));
            }
        } catch (e) { btnIsDissablefunc({ btnId, state: false }) }
    };

    function rowSelected() {

        var data = []
        return data = tableList.map((index) => {
            if ((index.selectCheck)) {
                return index.Item
            }
            return
        })
    }

    const PartyDropdown = () => {
        if (loginIsSCMCompany() === 1) {

            return null
        }
        return <Card>
            <CardBody className="c_card_body">
                <Row>
                    <Row>
                        <Col md="3">
                            <FormGroup className="mb-3">
                                <Label htmlFor="validationCustom01">{fieldLabel.Name}</Label>
                                <Col md="12">
                                    <Select
                                        name="Name"
                                        value={values.Name}
                                        isDisabled={pageMode === mode.assingLink ? true : false}
                                        isSearchable={true}
                                        className="react-dropdown"
                                        classNamePrefix="dropdown"
                                        options={supplierOptions}
                                        onChange={(hasSelect, evn) => {
                                            onChangeSelect({ hasSelect, evn, state, setState, })
                                            GoButton_Handler(hasSelect)
                                            dispatch(Breadcrumb_inputName(hasSelect.label
                                            ))
                                        }}
                                    />
                                    {isError.Name.length > 0 && (
                                        <span className="text-danger f-8"><small>{isError.Name}</small></span>
                                    )}

                                </Col>
                            </FormGroup>
                        </Col>
                        <Col md="3" className="mt-4">
                        </Col>
                    </Row>
                </Row>

            </CardBody>
        </Card>
    }

    let IsEditMode_Css = ''
    if ((modalCss) || (pageMode === mode.dropdownAdd)) { IsEditMode_Css = "-5.5%" };

    if (!(userAccState === '')) {
        return (
            <React.Fragment>
                <div className="page-content" style={{ marginTop: IsEditMode_Css }}>
                    <Container fluid>
                        <MetaTags> <title>{userAccess.PageHeading}| FoodERP-React FrontEnd</title></MetaTags>

                        <Card className="text-black">
                            <CardHeader className="card-header   text-black c_card_header" >
                                <h4 className="card-title text-black">{userAccState.PageDescription}</h4>
                                <p className="card-title-desc text-black">{userAccState.PageDescriptionDetails}</p>
                            </CardHeader>

                            <CardBody className=" vh-10 0 text-black" style={{ backgroundColor: "#whitesmoke" }} >

                                <PartyDropdown ></PartyDropdown>
                                <PaginationProvider
                                    pagination={paginationFactory(pageOptions)} >

                                    {({ paginationProps, paginationTableProps }) => (
                                        <ToolkitProvider
                                            keyField="Item"
                                            data={tableList}
                                            columns={tableColumns}
                                            search
                                        >
                                            {toolkitProps => (
                                                <React.Fragment>
                                                    <div className="table">
                                                        <BootstrapTable
                                                            keyField={"Item"}
                                                            bordered={true}
                                                            striped={false}
                                                            selectRow={selectAllCheck(rowSelected())}
                                                            noDataIndication={<div className="text-danger text-center ">Items Not available</div>}
                                                            classes={"table align-middle table-nowrap table-hover"}
                                                            headerWrapperClasses={"thead-light"}

                                                            {...toolkitProps.baseProps}
                                                            {...paginationTableProps}
                                                        />
                                                        {countlabelFunc(toolkitProps, paginationProps, dispatch, "MRP")}
                                                        {mySearchProps(toolkitProps.searchProps)}
                                                    </div>

                                                    <Row className="align-items-md-center mt-30">
                                                        <Col className="pagination pagination-rounded justify-content-end mb-2">
                                                            <PaginationListStandalone
                                                                {...paginationProps}
                                                            />
                                                        </Col>
                                                    </Row>
                                                </React.Fragment>
                                            )
                                            }
                                        </ToolkitProvider>
                                    )
                                    }

                                </PaginationProvider>


                                {/* {(tableList.length > 0) ? <div className="row save1" style={{ paddingBottom: 'center' }}> */}
                                <SaveButton
                                    pageMode={pageMode}
                                    userAcc={userAccState}
                                    module={"PartyItems"} onClick={SaveHandler}
                                />
                                {/* </div> */}
                                {/* : <div className="row save1"></div>} */}
                            </CardBody>

                        </Card>

                    </Container>
                </div>
            </React.Fragment >
        );
    }
    else {
        return (
            <React.Fragment></React.Fragment>
        )
    }

};
export default PartyItems
